export default class Article{constructor(i,t,n){this.link=i,this.image=t,this.description=n}render(){$(".articles").append(`\n            <li class="article">\n                <a href="${this.link}" target="_blank" style="background-image: url('${this.image}')">\n                    <figcaption>${this.description}</figcaption>\n                </a>\n            </li>\n        `)}}